// standard library includes
#include <algorithm>
#include <cmath>
#include <iostream>
#include <numeric>
#include <vector>

class ReconEast {
  public:
    /*!
      Returns the corrected cluster time in microseconds.

      @param  data set label
      @param  calorimeter index
      @param  cluster time in c.t.
      @param  horizontal position
      @param  vertical position
      @return corrected cluster time in microseconds
    */
    static double CorrectTime(
        const std::string p_label,
        const int         p_caloIndex,
        const double      p_time,
        const double      p_x,
        const double      p_y) {
      // the value to correct
      double value = p_time;

      // the dataset information
      const auto dataset = Information(p_label);

      // correct the time
      value += dataset.shift.at(p_caloIndex - 1);

      // correct the time
      value += TimeOffset(p_caloIndex, p_x, p_y);

      // convert from clock ticks to microseconds
      value *= 0.00125;

      // return the corrected value
      return value;
    }

    /*!
      Returns the corrected cluster energy in MeV.

      @param  data set label
      @param  calorimeter index
      @param  cluster energy in uncorrected MeV
      @return corrected cluster energy in MeV
    */
    static double CorrectEnergy(
        const std::string p_label,
        const int         p_caloIndex,
        const double      p_energy) {
      // the value to correct
      double value = p_energy;

      // the dataset information
      const auto dataset = Information(p_label);

      // calibrate the energy
      value *= dataset.calibration.at(p_caloIndex - 1);

      // return the corrected value
      return value;
    }

  private:
    // data set information
    typedef struct {
      std::vector<double> delta;
      std::vector<double> shift;
      std::vector<double> calibration;
    } DatasetInformation;

    /*!
      Returns the data set's information.

      @param  data set label
      @return data set information
    */
    static DatasetInformation Information(
        const std::string &p_label) {
      // the data set's information
      DatasetInformation dataset;

      // select the requested data set
      switch (Index(p_label)) {
        // 60 hour
        case 0: dataset.delta = {
                  -5.000424, -5.041032, -5.068580, -5.043398, -5.022227, -4.862961, -5.121124, -5.020302,
                  -5.099735, -4.928493, -5.018675, -5.053352, -4.842031, -5.048308, -4.976610, -5.031144,
                  -4.983779, -4.994487, -5.141676, -4.914069, -4.939584, -5.243513, -4.926387, -4.956590};
                break;

        // high kick
        case 1: dataset.delta = {
                  -5.016359, -5.051403, -5.054251, -5.047308, -5.019049, -4.858039, -5.119844, -5.019513,
                  -5.099185, -4.922199, -5.020488, -5.052725, -4.844108, -5.037678, -4.963465, -5.025089,
                  -4.977763, -5.054401, -5.136954, -4.907823, -4.923971, -5.234484, -4.931400, -4.957880};
                break;

        // 9 day
        case 2: dataset.delta = {
                  -5.015220, -5.043536, -5.058534, -5.063588, -5.003154, -4.862568, -5.120273, -5.022167,
                  -5.099931, -4.922541, -5.027984, -5.044903, -4.849512, -5.038009, -4.972590, -5.029909,
                  -4.989024, -5.063355, -5.140248, -4.899599, -4.921483, -5.237241, -4.930995, -4.962432};
                break;

        // end game
        case 3: dataset.delta = {
                  -5.116680, -5.027141, -5.081127, -5.082543, -5.010596, -4.889851, -5.148179, -5.048122,
                  -5.097147, -4.932442, -5.038128, -5.024857, -4.854329, -5.042397, -4.990120, -5.055896,
                  -5.001668, -5.072143, -5.153728, -4.894537, -4.951071, -5.248332, -4.932568, -4.947586};
                break;
      } // end select the requested data set

      // the true time difference between calorimeters
      double alpha = std::accumulate(dataset.delta.begin(), dataset.delta.end(), 0.0) / 24.0;

      // intanstiate the time correction per calorimeter
      dataset.shift = std::vector<double>(dataset.delta.size(), 0.0);

      // the running time correction
      double correction = 0.0;

      for (std::size_t i = 0; i < dataset.delta.size(); ++i) {
        // update the time correction
        dataset.shift.at((i + 21) % 24) = correction;

        // update the running time correction
        correction += dataset.delta.at((i + 21) % 24) - alpha;
      }

      // optimal energy threshold per calorimeter
      const std::vector<double> thresholds = {
        1628.90, 1505.87, 1559.39, 1564.91, 1368.78, 1516.91, 1543.76, 1533.00,
        1518.09, 1551.69, 1582.60, 1610.76, 1604.16, 1566.52, 1527.99, 1486.99,
        1519.95, 1588.24, 1554.94, 1525.47, 1455.70, 1473.98, 1522.46, 1548.07};

      // set the absolute energy calibration constants
      for (std::size_t i = 0; i < thresholds.size(); ++i) {
        dataset.calibration.push_back(1700.0 / thresholds.at(i));
      }

      // return the data set's information
      return dataset;
    } // end function Information

    /*!
      Returns the index of the data set.

      @param  data set phrase
      @return data set index
    */
    static int Index(
        const std::string &p_label) {
      // the available data set labels
      const std::vector<std::string> labels = {
        "60h",
        "hik",
        "9dy",
        "end"};

      // get the data set index
      const int index = std::distance(labels.begin(), std::find(labels.begin(), labels.end(), p_label));

      // throw exception if no data set index was found
      if (static_cast<std::size_t>(index) == labels.size()) {
        throw std::runtime_error("No data set index found.");
      }

      // return the data set index
      return index;
    } // end function Index

    /*!
      Returns the time offset to apply in c.t.

      @param  calorimeter index
      @param  horizontal position
      @param  vertical position
      @return time offset
    */
    static double TimeOffset(
        int    p_caloIndex,
        double p_x,
        double p_y) {
      // find the closest crystal inside the calorimeter
      if (std::floor(p_x) < 0) p_x = 0;
      if (std::floor(p_x) > 8) p_x = 8;
      if (std::floor(p_y) < 0) p_y = 0;
      if (std::floor(p_y) > 5) p_y = 5;

      // the crystal index
      int crystalIndex = (8 - std::floor(p_x)) + std::floor(p_y) * 9;

      // the time offset
      double offset =
          m_matrix1.at(p_caloIndex - 1).at(crystalIndex) -
          m_current.at(p_caloIndex - 1).at(crystalIndex);

      // return the time offset
      return offset;
    } // end function TimeOffset

    // fiber length constants
    static std::vector<std::vector<double>> m_current;
    static std::vector<std::vector<double>> m_matrix1;
};

